import React from "react";
import FlexWrapper from "../Elements/FlexWrapper";
import {useAppDispatch, useAppSelector} from "../../hooks/redux";
import LiElement from "../Elements/LiElement";
import {fetchCatCategories} from "../../store/reducers/ActionCreators";


export const SideBar = () => {
    const {cat} = useAppSelector(state => state.cat)
    const dispatch = useAppDispatch();
    const handleFetchCategory = () => {
        dispatch(fetchCatCategories())
    }
    return(
        <FlexWrapper background={"#cdb5e1"} justify={"flex-start"} width={"240px"} padding={"20px 0"}>
            {cat.map((isem, i)=> <LiElement key={i} onClick={handleFetchCategory}>{isem.name}</LiElement>)}
        </FlexWrapper>
    )
}
