import axios from "axios";
import {ICat, ICategories} from "../types/ICat";
import {createAsyncThunk} from "@reduxjs/toolkit";


export const fetchCatCategories = createAsyncThunk(
    'cat/fetchAll',
    async (_,thunkAPI) => {
        debugger
        try {
            const response = await axios.get<ICategories[]>('https://api.thecatapi.com/v1/categories');
            return response.data;
        }catch (e) {
           return thunkAPI.rejectWithValue('something went wrong')
        }

    }
)

export const fetchCats = createAsyncThunk(
    'cat/fetchCategory',
    async (_,thunkAPI) => {
        debugger
        try {
            const response = await axios.get<ICat[]>(`https://api.thecatapi.com/v1/images/search?limit=10&page=1&category_ids=1 `);
            return response.data;
        }catch (e) {
            return thunkAPI.rejectWithValue('something went wrong')
        }

    }
)
